import {Typography,Card,CardContent} from "@material-ui/core";
import React, {useEffect, useState} from "react";
import withAuth from "../components/WithAuth";
import WithAppBar from "../components/WithAppBar";
import IPageProps from "../interfaces/IPageProps";
import {firebase} from "../firebase/index";

function index(props:IPageProps){
    const [temp,setTemp] = useState(0);
    let tempData = firebase.database().ref("/users/"+props.user+"/greenhouse/temp");

    useEffect(()=>{
        tempData.on('value',data=>{
            setTemp(data.val());
        });
    },[]);
    return(
        <div>
            <div>
                <Typography variant="h4" component="h2">
                    Vaše skleníky
                </Typography>
                <div style={{display:"flex"}}>
                    <Card>
                        <CardContent>
                            <Typography variant={"body1"}>
                                Test skleník
                            </Typography>
                            <Typography color="textSecondary" gutterBottom>
                                Aktuální teplota: {temp}
                            </Typography>
                        </CardContent>
                    </Card>
                </div>
            </div>
        </div>
    );
}

export default withAuth((props:IPageProps)=>{return(
    <WithAppBar component={index} title={"Index"} componentProps={props}/>
)});
