import {useEffect} from "react";
import anime from "animejs";

function Loading(){
    useEffect(()=>{
        anime({
            targets:".loadingDot",
            translateY:"-100%",
            direction: 'alternate',
            easing:"easeOutCubic",
            delay: anime.stagger(250, {start: 0}),
            loop: true,
        })
    },[]);
return (
    <div>
        <div className="wrap">
            <div className="loadingDot"></div>
            <div className="loadingDot"></div>
            <div className="loadingDot"></div>
        </div>
        <style jsx global>{`
            .wrap{
            background:black;
            width:100%;
            height:100%;
            position:absolute;
            display:flex;
            align-items: center;
            justify-content: center;
        }

            .wrap > div{
            width:100px;
            height:100px;
            background:white;
            position:relative;
            margin:1rem;
            border-radius:100%;
        }"
        `}</style>
    </div>
)
}

export default Loading;
