import React, {ReactComponentElement, ReactElement, useEffect, useState} from 'react';
import router from 'next/router';

function OnlyDesktop(Component:React.ComponentType<any>) {
    const [content, setContent] = useState();
    const [touchDisplay, setTouchDisplay] = useState(false);

    useEffect(() => {
       setTouchDisplay(navigator.maxTouchPoints===1);
    });

    let render = () => {
        if(touchDisplay) {
            return <div>unsuported</div>
        } else {
            return Component;
        }
    };

    return (
        <React.Fragment>
            {render()}
        </React.Fragment>
    )
}

export default OnlyDesktop;
