import React, {useEffect, useState} from 'react';
import App from 'next/app';
import Head from 'next/head';
import {useRouter} from 'next/router';
import { ThemeProvider,makeStyles,useTheme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import {lightTheme,darkTheme} from '../components/Theme';
import CustomAppBar from "../components/CustomAppBar";
import {Switch,FormControlLabel,Box} from "@material-ui/core";
import {auth} from '../firebase/index';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButton: {
        marginRight: 36,
    },
    hide: {
        display: 'none',
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
        whiteSpace: 'nowrap',
    },
    drawerOpen: {
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    drawerClose: {
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        overflowX: 'hidden',
        width: theme.spacing(7) + 1,
        [theme.breakpoints.up('sm')]: {
            width: theme.spacing(9) + 1,
        },
    },
    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

export default function MyApp (props) {

        const [theme,setTheme] = useState(false);
        const [showBtn,setShowBtn] = useState(true);
        const themeReg = /theme=(\w+)/;
        const classes = useTheme();
        const router = useRouter();

    useEffect(()=> {
        const themeCookie = document.cookie.match(themeReg);

        if(themeCookie)
            setTheme(JSON.parse(themeCookie[1]));

        // Remove the server-side injected CSS.
        const jssStyles = document.querySelector('#jss-server-side');
        if (jssStyles) {
            jssStyles.parentElement.removeChild(jssStyles);
        }
    },[]);

    const switchTheme = () => {
        document.cookie = 'theme='+(!theme);
        setTheme(!theme);
    };

    function toggleThemeSwitch(value) {
        setShowBtn(value);
    };

    const { Component, pageProps } = props;

        return (
            <React.Fragment>
                <Head>
                    <title>My page</title>
                    <meta name="viewport" content="minimum-scale=1, initial-scale=1, width=device-width" />
                </Head>
                <ThemeProvider theme={theme?darkTheme:lightTheme}>
                    {/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
                    {/*<div style={
                        {display: "flex",
                        flexFlow: "column",
                        height: "100vh"}
                    }>*/}
                    <CssBaseline />
                    <div test className={classes.root}>
                    {showBtn?<CustomAppBar text={"Some name"}/>:null}
                    <Box height={"100%"}>
                        <Component {...pageProps} theme={theme} setThemeBtn={toggleThemeSwitch} />
                    </Box>
                    </div>
                    {showBtn?
                        <FormControlLabel labelPlacement={"start"} label={"Dark mode"} control={<Switch checked={theme} onChange={switchTheme.bind(this)}/>}/>
                        :null}
                    {/*</div>*/}

                </ThemeProvider>
            </React.Fragment>
        );

}
