import React, {useEffect, useState} from 'react';
import App from 'next/app';
import Head from 'next/head';
import {useRouter} from 'next/router';
import { ThemeProvider } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import {lightTheme,darkTheme} from '../components/Theme';
import CustomAppBar from "../components/CustomAppBar";
import {Switch,FormControlLabel,Box} from "@material-ui/core";
import {auth} from '../firebase/index';

export default function MyApp (props) {

        const [state,setState] = useState({theme: false,status:"LOADING",showBtn:true});
        const themeReg = /theme=(\w+)/;
        const router = useRouter();

    useEffect(()=> {

        auth.onAuthStateChanged(authUser => {
            console.log(authUser);
            if(authUser) {
                setState({
                    status: 'SIGNED_IN'
                });
            } else {
                router.push({pathname: "/login", query: {refresh: 'false'}});
            }
        });

        // Remove the server-side injected CSS.
        const jssStyles = document.querySelector('#jss-server-side');
        if (jssStyles) {
            jssStyles.parentElement.removeChild(jssStyles);
        }
    });

    const switchTheme = () => {
        document.cookie = 'theme='+(!state.theme);
        setState({
            theme:!state.theme
        });
    };

    function toggleThemeSwitch(value) {
        setState({
            showBtn:value
        });
    };

    const { Component, pageProps } = props;

        return (
            <React.Fragment>
                <Head>
                    <title>My page</title>
                    <meta name="viewport" content="minimum-scale=1, initial-scale=1, width=device-width" />
                </Head>
                <ThemeProvider theme={state.theme?darkTheme:lightTheme}>
                    {/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
                    {/*<div style={
                        {display: "flex",
                        flexFlow: "column",
                        height: "100vh"}
                    }>*/}
                    <CssBaseline />
                    {state.showBtn?<CustomAppBar text={"Some name"}/>:null}
                    <Box height={"100%"}>
                        <Component {...pageProps} setThemeBtn={toggleThemeSwitch} />
                    </Box>
                    {this.state.showBtn?
                        <FormControlLabel labelPlacement={"start"} label={"Dark mode"} control={<Switch checked={state.theme} onChange={switchTheme.bind(this)}/>}/>
                        :null}
                    {/*</div>*/}
                </ThemeProvider>
            </React.Fragment>
        );

}
