import React, {useEffect, useRef, useState} from "react";
import anime from "animejs";
import {firebase} from "../firebase/index";
function Termostat(props){
    const min = 0;
    const max = 50;
    const minAngle = 0;
    const maxAngle = 180;
    const segCount = 22//11;
    let handle = false;
    const lines = useRef();
    const valueInput = useRef();
    const hand = useRef();
    let [containers,setContainers] = useState();
    let [lastNode,setLastNode] = useState();
    let [lastNodeAngle,setLastNodeAngle] = useState(0);
    const updateHandAngle = (angle) => {
        angle = Math.round(angle);

        if(angle <= maxAngle && angle >= minAngle){
            valueInput.current.value = Math.round((max/maxAngle)*angle);
            for(let i = 0; i < containers.length; i++){

                let containerAngle = parseInt(containers[i].getAttribute('rotate'));
                if((containerAngle > lastNodeAngle && containerAngle <= angle) || (containerAngle < lastNodeAngle && angle <= containerAngle)) {
                    anime({
                        targets: lastNode,
                        scale: [
                            {value: 1, easing: 'easeInOutQuad', duration: 500}
                        ],
                        delay: 0,
                    });
                    anime({
                        targets: containers[i].childNodes,
                        scale: [
                            {value: 1.5, easing: 'easeOutSine', duration: 500}
                        ],
                        delay: 0,
                    });
                    setLastNode(containers[i].childNodes[0]);
                    setLastNodeAngle(containerAngle);
                    break;
                }

            };
            hand.current.style.transform="rotate("+(angle)+"deg)";
        }
    }

    useEffect(()=>{
        let j = 0;
        for(let i = 0; i < segCount; i++){
            let handAngle = Math.round((maxAngle/max)*j);
            let line = '<div rotate='+handAngle+' style="transform:rotate('+handAngle+'deg)!important;" class="lineContainer"><div class="line"></div></div>';
            lines.current.innerHTML += line;
            j+=max/(segCount-1);
        }

        setContainers(document.getElementsByClassName('lineContainer'));
        setLastNode(containers[0].childNodes[0]);
        setLastNodeAngle(0);
        anime({
            targets: lastNode,
            scale: [
                {value: 1.5, easing: 'easeOutSine', duration: 500}
            ],
            delay: 0,
        });
    },[]);

    return(
        <div>
            <div id="work" className="workspace">
                <svg id="handPath" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 161.45 161.45">
                    <defs>
                        <style>{".pathcircle{fill:none;stroke:#eceff1;stroke-miterlimit:10;stroke-opacity:0.08;stroke-width:2px;}"}</style>
                    </defs>
                    <title>Datový zdroj 22</title>
                    <g id="Vrstva_2" data-name="Vrstva 2">
                        <g id="Vrstva_2-2" data-name="Vrstva 2">
                            <circle className="pathcircle" cx="80.73" cy="80.73" r="80.6" stroke-dasharray="253" stroke-dashoffset="253"/>
                        </g>
                    </g>
                </svg>
                <div ref={lines} id="lines">
                </div>
                <div ref={hand}>
                    <svg id="handle" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 26.04 26.04">
                        <defs>
                            <style>{".handle{fill:#2196f3;}"}</style>
                        </defs>
                        <title>Datový zdroj 25</title>
                        <g id="Vrstva_2" data-name="Vrstva 2">
                            <g id="Vrstva_2-2" data-name="Vrstva 2">
                                <circle className="handle" cx="13.02" cy="13.02" r="13.02"/>
                            </g>
                        </g>
                    </svg>
                </div>
            </div>
            <input ref={valueInput} type = "number"></input>
            <style jsx global>{`
                .workspace{
                  background:#212121;
                  height:500px;
                  width:500px;
                  margin:auto;
                  position:relative;
                  border-radius:100%;
                }
                #lines,#handPath,.lineContainer{
                  pointer-events: none;
                  overflow:visible;
                }
                #handPath{
                  width:5rem;
                  height:5rem;
                }
                #lines{
                  position:absolute;
                  height:0px;
                  width:500px;
                  margin-top:50%;
                }
                .lineContainer{
                  width: 250px;
                  position:absolute;
                  transform-origin: right center;
                }
                .line{
                  height:5px;
                  width:3rem;
                  margin-left:1.5rem;
                }
                #handPath{
                  width: 250px;
                    height: 250px;
                    margin: 50%;
                    transform: translate(-50%, -50%);
                    position: absolute;
                }
                #hand{
                  margin-top:50%;
                  background:red;
                  transform-origin: right center;
                  position:absolute;
                  width:250px;
                  height:0;
                }
                #handle{
                  width:2rem;
                  height:2rem;
                  transform:translate(50%,-50%);
                  right:125px;
                  position:absolute;
                }
            `}</style>
        </div>
    )
}

export default Termostat;
