import React from 'react';
import App from 'next/app';
import Head from 'next/head';
import Router from 'next/router';
import { ThemeProvider } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import {lightTheme,darkTheme} from '../components/Theme';
import CustomAppBar from "../components/CustomAppBar";
import {Switch,FormControlLabel,Box} from "@material-ui/core";

export default class MyApp extends App {
    constructor(props) {
        super(props);
        this.state = {theme: false,showBtn:true};
        this.themeReg = /theme=(\w+)/;
    }

    componentDidMount() {

        // Remove the server-side injected CSS.
        const jssStyles = document.querySelector('#jss-server-side');
        if (jssStyles) {
            jssStyles.parentElement.removeChild(jssStyles);
        }
    }

    switchTheme(){
        this.setState((state) => {
            document.cookie = 'theme='+(!this.state.theme);
            return {theme:!state.theme}
        });
    }

    toggleThemeSwitch(value){
        this.setState((state) => {
            return {showBtn:value}
        });
    }

    changeSession(value){
        this.setState((state) => {
            document.cookie = "sessionID-NEKRAST!=" + value.session;
            document.cookie = "edit="+value.edit;
            return {session: value.session, edit: value.edit}
        });
    }

    render() {
        const { Component, pageProps } = this.props;

        return (
            <React.Fragment>
                <Head>
                    <title>My page</title>
                    <meta name="viewport" content="minimum-scale=1, initial-scale=1, width=device-width" />
                </Head>
                <ThemeProvider theme={this.state.theme?darkTheme:lightTheme}>
                    {/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
                    {/*<div style={
                        {display: "flex",
                        flexFlow: "column",
                        height: "100vh"}
                    }>*/}
                    <CssBaseline />
                    {this.state.showBtn?<CustomAppBar text={"Some name"}/>:null}
                    <Box height={"100%"}>
                        <Component {...pageProps} session={this.state.session} edit={this.state.edit} changeSess={this.changeSession.bind(this)} setThemeBtn={this.toggleThemeSwitch.bind(this)} />
                    </Box>
                    {this.state.showBtn?
                        <FormControlLabel labelPlacement={"start"} label={"Dark mode"} control={<Switch checked={this.state.theme} onChange={this.switchTheme.bind(this)}/>}/>
                        :null}
                    {/*</div>*/}
                </ThemeProvider>
            </React.Fragment>
        );
    }
}
