import React, {useEffect, useState} from 'react';
import App from 'next/app';
import Head from 'next/head';
import {useRouter} from 'next/router';
import { ThemeProvider,makeStyles,useTheme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import {lightTheme,darkTheme} from '../components/Theme';
import CustomAppBar from "../components/CustomAppBar";
import {
    Switch,
    FormControlLabel,
    Box,
    Divider,
    Drawer,
    List,
    ListItem,
    ListItemIcon,
    ListItemText,
    IconButton,
    AppBar, Toolbar, Typography
} from "@material-ui/core";
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import clsx from 'clsx';
import ChevronRightIcon from '@material-ui/icons/ChevronRight'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft'
import InboxIcon from '@material-ui/icons/Inbox'
import MailIcon from '@material-ui/icons/Mail'
import {auth} from '../firebase/index';
import MenuIcon from "@material-ui/core/SvgIcon/SvgIcon";
import Link from "next/link";
const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    appBar: {
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButton: {
        marginRight: 36,
    },
    hide: {
        display: 'none',
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
        whiteSpace: 'nowrap',
    },
    drawerOpen: {
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    drawerClose: {
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        overflowX: 'hidden',
        width: theme.spacing(7) + 1,
        [theme.breakpoints.up('sm')]: {
            width: theme.spacing(9) + 1,
        },
    },
    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

export default function MyApp (props) {

        const [theme,setTheme] = useState(false);
        const [showBtn,setShowBtn] = useState(true);
        const [open, setOpen] = React.useState(true);

        const handleDrawerOpen = () => {
            setOpen(true);
        };

        const handleDrawerClose = () => {
            setOpen(false);
        };

        const handleLogout = () => {
            auth.signOut().then(function() {
                alert('Logout successful');
            }).catch(function(error) {
                alert('OOps something went wrong check your console');
                console.log(err);
            });
        };

        const themeReg = /theme=(\w+)/;
        const classes = useTheme();
        const router = useRouter();

    useEffect(()=> {
        const themeCookie = document.cookie.match(themeReg);

        if(themeCookie)
            setTheme(JSON.parse(themeCookie[1]));

        // Remove the server-side injected CSS.
        const jssStyles = document.querySelector('#jss-server-side');
        if (jssStyles) {
            jssStyles.parentElement.removeChild(jssStyles);
        }
    },[]);

    const switchTheme = () => {
        document.cookie = 'theme='+(!theme);
        setTheme(!theme);
    };

    function toggleThemeSwitch(value) {
        setShowBtn(value);
    };

    const { Component, pageProps } = props;

        return (
            <React.Fragment>
                <Head>
                    <title>My page</title>
                    <meta name="viewport" content="minimum-scale=1, initial-scale=1, width=device-width" />
                </Head>
                <ThemeProvider theme={theme?darkTheme:lightTheme}>
                    {/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
                    {/*<div style={
                        {display: "flex",
                        flexFlow: "column",
                        height: "100vh"}
                    }>*/}
                    <CssBaseline />
                    <div id={"test"} className={classes.root}>
                    {/*showBtn?<CustomAppBar open={open} handleDrawerOpen={handleDrawerOpen} text={"Some name"}/>:null*/}
                        <AppBar position="static"  className={clsx(classes.appBar, {
                            [classes.appBarShift]: props.open,
                        })}>
                            <Toolbar variant="dense" >
                                <IconButton
                                    onClick={handleDrawerOpen}
                                    edge="start"
                                    className={clsx(classes.menuButton, {
                                        [classes.hide]: open,
                                    })}
                                    color="inherit"
                                    aria-label="open drawer"
                                >
                                    <MenuIcon />
                                </IconButton>
                                <Typography className={classes.title} variant="h5" noWrap component={"div"}>
                                    {props.text}
                                </Typography>
                                <div className={classes.grow}/>
                                <Link href={{pathname:"/login", query:{refresh:'false'}}}>
                                    <IconButton aria-label="display more actions" edge="end" color="inherit">
                                        <VpnKeyIcon />
                                    </IconButton>
                                </Link>
                                <IconButton onClick={handleLogout} aria-label="display more actions" edge="end" color="inherit">
                                    <VpnKeyIcon />
                                </IconButton>
                            </Toolbar>
                        </AppBar>
                        <Drawer variant="permanent" className={clsx(classes.drawer, {
                            [classes.drawerOpen]: open,
                            [classes.drawerClose]: !open,
                        })}
                                classes={{
                                    paper: clsx({
                                        [classes.drawerOpen]: open,
                                        [classes.drawerClose]: !open,
                                    }),
                                }}>
                            <div className={classes.toolbar}>
                                <IconButton onClick={handleDrawerClose}>
                                    {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
                                </IconButton>
                            </div>
                            <Divider />
                            <List>
                                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                                    <ListItem button key={text}>
                                        <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                        <ListItemText primary={text} />
                                    </ListItem>
                                ))}
                            </List>
                            <Divider />
                            <List>
                                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                                    <ListItem button key={text}>
                                        <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                        <ListItemText primary={text} />
                                    </ListItem>
                                ))}
                            </List>
                        </Drawer>
                        <main className={classes.content}>
                            <div className={classes.toolbar} ></div>
                            <Component {...pageProps} theme={theme} setThemeBtn={toggleThemeSwitch} />
                        </main>
                    </div>
                    {showBtn?
                        <FormControlLabel labelPlacement={"start"} label={"Dark mode"} control={<Switch checked={theme} onChange={switchTheme.bind(this)}/>}/>
                        :null}
                    {/*</div>*/}

                </ThemeProvider>
            </React.Fragment>
        );

}
