import Link from "next/link";
import useSWR from 'swr';
import {DatePicker,MuiPickersUtilsProvider } from '@material-ui/pickers';
import {createMuiTheme, makeStyles, ThemeProvider} from '@material-ui/core/styles';
import DateFnsUtils from '@date-io/date-fns';
import React, {useEffect, useState} from "react";
import {orange} from "@material-ui/core/colors";
import {useRouter} from "next/router";
import {Typography} from "@material-ui/core";
import withAuth from "../components/WithAuth";
import Tempmeter from "../components/Tempmeter";
import {firebase} from "../firebase/index.js";

const useStyle = makeStyles(theme=>({
    center:{
        display:'flex'
    },
    tempmeter:{
        margin:'50px'
    }
}));

function Index(props) {
    const classes = useStyle();
    const [temp,setTemp] = useState(0);
    let tempData = firebase.database().ref("/greenhouse/temp");

    useEffect(()=>{
        tempData.on('value',data=>{
            setTemp(data.val());
        });
    },[]);

 return(
     <div className={classes.center}>
         {props.session!==null?<Typography className={classes.header} variant="h4">{props.session }</Typography>:null}
         <div className={classes.tempmeter}>
            <Tempmeter theme={props.theme} temp={0} />
         </div>
     </div>
 )
}

export default withAuth(Index);
