import React, {useEffect, useRef, useState} from "react";
import anime from "animejs";
import {firebase} from "../firebase/index";
function Termostat(props){
    const maxHeight = 8;
    const minHeight = 215;
    const height = (minHeight-maxHeight);
    const maxTemp = 50;
    const minTemp = -50;
    //const range = Math.abs(minTemp)+maxTemp;
    let temp = props.temp;
    let rangeVal = temp>=0?((50/maxTemp)*temp)+50:50-((50/Math.abs(minTemp))*Math.abs(temp));
    let y = height-(((height/100)*rangeVal)-8);

    const text = useRef();
    const tempValue = useRef();
    const textLine = useRef();
    const water = useRef();

    const duration = 5000;
    const easing = "spring(1, 82, 12, 0)";
    const [prevText,setPrevtext] = useState(minTemp);

    useEffect(()=>{
        var textAnim = {
            text:prevText
        };

        anime({
            targets:textAnim,
            text:temp,
            duration:duration,
            easing:easing,
            round:1,
            update:function(){
                text.current.innerHTML = textAnim.text+"Â°C";
            },
            complete:function (anim) {
                setPrevtext(temp);
            }
        });

        anime({
            targets:tempValue.current,
            y2:y,
            duration:duration,
            easing:easing,
            update:function(){

            }
        });

        anime({
            targets:textLine.current,
            y2:y,
            y1:y,
            duration:duration,
            easing:easing,
            update:function(){

            }
        });

        anime({
            targets:text.current,
            transform:"translate(55.59 "+(y-2.77)+")",
            duration:duration,
            easing:easing,
            update:function(){

            }
        });

        anime({
            targets:water.current,
            d:"M 0.14 16.09 C 0.14 16.09 21.14 20.25 22.49 12.8 C 23.84 5.35 17.37 0.13 9.87 0.13 C 2.37 0.13 0.14 16.09 0.14 16.09 Z",
            duration:1000,
            easing:'linear',
            direction: 'alternate',
            loop:true
        });

    },[props.temp]);

    return(
        <div>
        <div id="work" className="workspace">
            <svg id="handPath" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 161.45 161.45">
                <defs>
                    <style>{".cls-1{fill:none;stroke:#eceff1;stroke-miterlimit:10;stroke-opacity:0.08;stroke-width:2px;}"}</style>
                </defs>
                <title>DatovÃ½ zdroj 22</title>
                <g id="Vrstva_2" data-name="Vrstva 2">
                    <g id="Vrstva_2-2" data-name="Vrstva 2">
                        <circle className="cls-1" cx="80.73" cy="80.73" r="80.6" stroke-dasharray="253"
                                stroke-dashoffset="253"/>
                    </g>
                </g>
            </svg>
            <div id="lines">
            </div>
            <div id="hand">
                <svg id="handle" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 26.04 26.04">
                    <defs>
                        <style>{".handle{fill:#2196f3;}"}</style>
                    </defs>
                    <title>DatovÃ½ zdroj 25</title>
                    <g id="Vrstva_2" data-name="Vrstva 2">
                        <g id="Vrstva_2-2" data-name="Vrstva 2">
                            <circle className="handle" cx="13.02" cy="13.02" r="13.02"/>
                        </g>
                    </g>
                </svg>
            </div>
        </div>
        <input type = "number" id = "valueInput" > </input>
        </div>
    )
}

export default Termostat;
