import React, {useEffect, useRef, useState,MouseEvent,SyntheticEvent} from "react";
import anime from "animejs";
import {firebase} from "../firebase";
import ITermostat from "../interfaces/ITermostat";
import {number} from "prop-types";
import {useTheme} from "@material-ui/styles";

function Termostat(props:ITermostat){
    const min = 0;
    const max = 50;
    const componentWidth = 500;
    const componentHeight = 500;
    const minAngle = 0;
    const maxAngle = 270;
    const angleOverlap = 45;
    const segCount = 22//11;
    const theme = useTheme();
    // @ts-ignore
    const handleColor = theme.palette.primary.main;

    const lines = useRef<HTMLDivElement>(null);
    const valueInput = useRef<HTMLInputElement>(null);
    const hand = useRef<HTMLDivElement>(null);
    const handle = useRef<SVGSVGElement>(null);

    const [containers,setContainers] = useState<HTMLCollectionOf<Element>>();

    const [lastNode,setLastNode] = useState<ChildNode>();
    const [lastNodeAngle,setLastNodeAngle] = useState<number>(0);
    const [angleTrans,setAngleTrans] = useState<number>(0);
    const [currentAngle,setCurrentAngle] = useState<number>(0);
    const [handAngle,setHandAngle] = useState<number>(0);
    const [currentValue,setCurrentValue] = useState<number>(0);
    const [drag,setDrag] = useState<boolean>(false);
    const [initialize,setInitialize] = useState<boolean>(true);

    const workspaceSize = {
        height:componentHeight+"px",
        width:componentWidth+"px"
    };

    const linesSize = {
        width:componentWidth+"px"
    };

    const lineContainerSizes = {
        width: componentWidth/2+"px"
    };

    const handPathSize = {
        width: componentWidth/3+"px",
        height: componentHeight/3+"px"
    };

    const handSize = {
        width: componentWidth/2+"px"
    };

    const handleSize = {
        right: (componentWidth/2)/3+"px"
    };

    const handPathCircle = {
        circleRadius:2*Math.PI*80.6,
        transform:"rotate("+angleOverlap+"deg)",
        maxRadius:(2*Math.PI*80.6)-(((2*Math.PI*80.6)/360)*maxAngle)
    };

    function inputUpdated(e:any){
        let val = e.target.value;
        setCurrentValue(val);
        setCurrentAngle((((maxAngle)/max)*val)+(angleOverlap));
    }

    function onMove(e:MouseEvent){
        if(drag){
            let angle = Math.atan2(e.nativeEvent.offsetY-(componentHeight/2), e.nativeEvent.offsetX-(componentWidth/2));
            let angle2 = angle * (180/Math.PI);
            //to 360 range
            if(angle2 < 0)
            {
                angle2 = 360-(-angle2);
            }
            //set 0 angle
            angle2+= 180;
            //corect angle
            if(angle2 > 360)
                angle2-=360

            if(angle2 >= (0+angleOverlap) && angle2 <= (maxAngle+angleOverlap)) {
                setCurrentValue(Math.round(((max / maxAngle) * angle2)-(max / maxAngle) * angleOverlap));
                setAngleTrans(angle2);
                setHandAngle(angle2);
            }
        }
    }

    function onDrag(e:MouseEvent){
        if(!drag) {
            setDrag(true);
            if(handle.current)
            handle.current.style.pointerEvents= "none";
        }
    }

    function onDragEnd(e:MouseEvent){
        if(drag){
            setDrag(false);
            if(handle.current)
            handle.current.style.pointerEvents="all";
        }
    }

    useEffect(()=>{
        if(containers) {
            let angle = Math.round(handAngle);
            if (angle <= (maxAngle+angleOverlap) && angle >= (minAngle+angleOverlap)) {
                for (let i = 0; i < containers.length; i++) {

                    let containerAngle = parseInt(containers[i].getAttribute('rotate') as string);
                    if ((containerAngle > lastNodeAngle && containerAngle <= angle) || (containerAngle < lastNodeAngle && angle <= containerAngle)) {
                        anime({
                            targets: lastNode,
                            scale: [
                                {value: 1, easing: 'easeInOutQuad', duration: 500}
                            ],
                            delay: 0,
                        });
                        anime({
                            targets: containers[i].childNodes[0],
                            scale: [
                                {value: 1.5, easing: 'easeOutSine', duration: 500}
                            ],
                            delay: 0,
                        });
                        setLastNode(containers[i].childNodes[0]);
                        setLastNodeAngle(containerAngle);
                        break;
                    }

                }
                ;
                if(hand.current)
                hand.current.style.transform = "rotate(" + (angle) + "deg)";
            }
        }
    },[handAngle]);

    useEffect(()=>{
        let anim = {
            value:angleTrans
        };

        anime({
            targets:anim,
            value:(currentAngle),
            easing:"linear",
            duration:500,
            update:function(){
                //updateHandAngle(anim.value);
                setHandAngle(anim.value);
            },
            complete:()=>{
                setAngleTrans(currentAngle);
            }
        });
    },[currentAngle]);

    useEffect(()=>{
        if(valueInput.current)
        valueInput.current.value = currentValue.toString();
        if(!initialize)
        props.onValueChanged(currentValue.toString());
    },[currentValue]);

    useEffect(()=>{
        let initValue = props.defaultValue;
        if(initValue!=undefined) {
            let j = 0;
            for (let i = 0; i < segCount; i++) {
                let handAngle = Math.round((maxAngle / max) * j)+angleOverlap;
                let line = '<div rotate=' + handAngle + ' style="transform:rotate(' + handAngle + 'deg)!important;width:'+(lineContainerSizes.width)+';" class="lineContainer">' +
                                '<div class="line"></div>' +
                                '<div class="valueGroup">' +
                                    '<div class="valueLine"></div>' +
                                    '<div class="value" style="transform:rotate(' + -handAngle + 'deg)">'+Math.round(j)+'</div>' +
                                '</div>'+
                           '</div>';
                if (lines.current)
                    lines.current.innerHTML += line;
                j += max / (segCount - 1);
            }

            let contData = document.getElementsByClassName('lineContainer');
            let initlastNode = contData[0].childNodes[0];
            let initLastNodeAngle = 0;
            anime({
                targets: contData[0].childNodes[0],
                scale: [
                    {value: 1.5, easing: 'easeOutSine', duration: 500}
                ],
                delay: 0,
            });

            let lineSegs = document.getElementsByClassName("line");
            const first = [73, 80, 245];
            const second = [244, 67, 54];

            for (let i = 0; i < lineSegs.length; i++) {
                var percent = ((i) / (lineSegs.length - 1));

                var red = first[0] + percent * (second[0] - first[0]);
                var green = first[1] + percent * (second[1] - first[1]);
                var blue = first[2] + percent * (second[2] - first[2]);

                lineSegs[i].setAttribute("style", "background:" + "rgb(" + red + "," + green + "," + blue + ");");
            }
            ;

            setContainers(contData);
            const updateHandAngle = (angle: number) => {
                angle = Math.round(angle);

                if (angle <= maxAngle && angle >= minAngle) {
                    for (let i = 0; i < contData.length; i++) {

                        let containerAngle = parseInt(contData[i].getAttribute('rotate') as string);
                        if ((containerAngle > initLastNodeAngle && containerAngle <= angle) || (containerAngle < initLastNodeAngle && angle <= containerAngle)) {
                            anime({
                                targets: initlastNode,
                                scale: [
                                    {value: 1, easing: 'easeInOutQuad', duration: 500}
                                ],
                                delay: 0,
                            });
                            anime({
                                targets: contData[i].childNodes[0],
                                scale: [
                                    {value: 1.5, easing: 'easeOutSine', duration: 500}
                                ],
                                delay: 0,
                            });
                            initlastNode = contData[i].childNodes[0];
                            initLastNodeAngle = containerAngle;
                            break;
                        }

                    }
                    ;
                    //document.getElementById("hand").style.transform="rotate("+(angle)+"deg)";
                }
            };

            let initAngleTrans = {
                value: 0
            }

            anime({
                targets: initAngleTrans,
                value: ((maxAngle / max) * initValue),
                easing: "linear",
                duration: 500,
                update: function () {
                    updateHandAngle(initAngleTrans.value);
                },
                complete: function () {
                    setLastNode(initlastNode);
                    setLastNodeAngle(initLastNodeAngle);

                    if(initValue!=undefined) {
                        setCurrentValue(initValue);
                        setCurrentAngle(((maxAngle / max) * initValue)+angleOverlap);
                    }

                    setInitialize(false);
                }
            });
        }
    },[props.defaultValue]);

    return(
        <div>
            <div id="work" onMouseMove={onMove} onMouseUp={onDragEnd} style={workspaceSize} className="workspace">
                <svg id="handPath" style={handPathSize} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 161.45 161.45">
                    <defs>
                        <style>{".pathcircle{fill:none;stroke:"+(props.theme==1?"white":"black")+";stroke-miterlimit:10;stroke-opacity:0.08;stroke-width:3px;}"}</style>
                    </defs>
                    <title>Datov zdroj 22</title>
                    <g id="Vrstva_2" data-name="Vrstva 2">
                        <g id="Vrstva_2-2" data-name="Vrstva 2">
                            <circle className="pathcircle" cx="80.73" cy="80.73" r="80.6" style={{transform:"translate(-50%,-50%)"+handPathCircle.transform}} strokeDasharray={handPathCircle.circleRadius} strokeDashoffset={handPathCircle.maxRadius}/>
                        </g>
                    </g>
                </svg>
                <div ref={lines} id="lines" style={linesSize}>
                </div>
                <div ref={hand} id="hand" style={handSize}>
                    <svg ref={handle} onMouseDown={onDrag} id="handle" style={handleSize} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 26.04 26.04">
                        <defs>
                            <style>{".handle{fill:"+handleColor+"}"}</style>
                        </defs>
                        <title>Datov zdroj 25</title>
                        <g id="Vrstva_2" data-name="Vrstva 2">
                            <g id="Vrstva_2-2" data-name="Vrstva 2">
                                <circle className="handle" cx="13.02" cy="13.02" r="13.02"/>
                            </g>
                        </g>
                    </svg>
                </div>
            </div>
            <input ref={valueInput} onBlur={inputUpdated} type = "number"></input>
            <style>{".valueLine{background:"+(props.theme==1?"white":"black")+"}"}</style>
            <style jsx global>{`
                .workspace{
                  background:/*#212121*/none;
                  //height:500px;
                  //width:500px;
                  margin:auto;
                  position:relative;
                  border-radius:100%;
                }
                #lines,#handPath,.lineContainer{
                  pointer-events: none;
                  overflow:visible;
                }
                #handPath{
                  width:5rem;
                  height:5rem;
                }
                #lines{
                  position:absolute;
                  height:0px;
                  //width:500px;
                  margin-top:50%;
                }
                .lineContainer{
                  //width: 250px;
                  height:0;
                  position:absolute;
                  display:flex;
                  align-items:center;
                  flex-direction:row;
                  transform-origin: right center;
                }
                .line{
                  height:5px;
                  width:3rem;
                  margin-left:1.5rem;
                }
                .valueLine{
                  height: 2px;
                  width: 1rem;
                  margin-left: 1rem;
                  opacity:0.2;
                }
                .valueGroup{
                  display: flex;
                  flex-direction: row;
                  align-items: center;
                }
                .value{
                    margin-left:0.5rem;
                }
                #handPath{
                  //width: 250px;
                  //height: 250px;
                  margin: 50%;
                  transform: rotate(180deg);
                  position: absolute;
                }
                #hand{
                  margin-top:50%;
                  background:red;
                  transform-origin: right center;
                  position:absolute;
                  //width:250px;
                  height:0;
                }
                #handle{
                  cursor: grab;
                  width:2rem;
                  height:2rem;
                  transform:translate(50%,-50%);
                  right: 50%;
                  position:absolute;
                }
            `}</style>
        </div>
    )
}

export default Termostat;
