import React, {useEffect, useRef, useState} from "react";
import anime from "animejs";
import {firebase} from "../firebase/index";
function Tempmeter(props){
    const maxHeight = 8;
    const minHeight = 215;
    const height = (minHeight-maxHeight);
    const maxTemp = 50;
    const minTemp = -50;
    //const range = Math.abs(minTemp)+maxTemp;
    let temp = props.temp;
    let rangeVal = temp>=0?((50/maxTemp)*temp)+50:50-((50/Math.abs(minTemp))*Math.abs(temp));
    let y = height-(((height/100)*rangeVal)-8);

    const text = useRef();
    const tempValue = useRef();
    const textLine = useRef();

    const duration = 5000;
    const easing = "spring(1, 82, 12, 0)";
    const [prevText,setPrevtext] = useState(minTemp);

    useEffect(()=>{
        var textAnim = {
            text:prevText
        };

        anime({
            targets:textAnim,
            text:temp,
            duration:duration,
            easing:easing,
            round:1,
            update:function(){
                text.current.innerHTML = textAnim.text+"Â°C";
            },
            complete:function (anim) {
                setPrevtext(temp);
            }
        });

        anime({
            targets:tempValue.current,
            y2:y,
            duration:duration,
            easing:easing,
            update:function(){

            }
        });

        anime({
            targets:textLine.current,
            y2:y,
            y1:y,
            duration:duration,
            easing:easing,
            update:function(){

            }
        });

        anime({
            targets:text.current,
            transform:"translate(33.17 "+(y-2.77)+")",
            duration:duration,
            easing:easing,
            update:function(){

            }
        });
    },[props.temp]);

    return(
        <div>
        <svg xmlns="http://www.w3.org/2000/svg" width={"100"} viewBox="0 0 61.02 246.88">
            <defs>
                <style>{".cls-1,.cls-3,.cls-5{fill:none;stroke-miterlimit:10;}.cls-1{stroke:"+(props.theme==1?"white":"black")+";stroke-width:0.25px;}.cls-2{fill:#4950f5;}.cls-3{stroke:#4950f5;stroke-linecap:round;stroke-width:7px;}.cls-4{font-size:12px;fill:"+(props.theme==1?"white":"black")+";font-family:CenturyGothic, Century Gothic;}.cls-5{stroke:#1d1d1b;}"}</style>
            </defs>
            <title>DatovÃ½ zdroj 2</title>
            <g id="Vrstva_2" data-name="Vrstva 2">
                <g id="Vrstva_2-2" data-name="Vrstva 2">
                    <path className="cls-1" d="M23.88,215.39a16.51,16.51,0,1,1-14.46,0"/>
                    <circle className="cls-2" cx="16.65" cy="230.23" r="12.65"/>
                    <path className="cls-1"
                          d="M23.88,215.39V7.35A7.23,7.23,0,0,0,16.65.13h0A7.23,7.23,0,0,0,9.42,7.35v208"/>
                    <line ref={tempValue} className="cls-3" x1="16.65" y1="230.23" x2="16.64" y2={minHeight}/>
                    <line ref={textLine} className="cls-1" x1="60.39" y1={minHeight} x2="16.65" y2={minHeight}/>
                    <text ref={text} className="cls-4" transform={"translate(33.17 "+(minHeight-2.77)+")"}>{"Waiting..."}</text>
                    <polyline className="cls-5" points="4.29 227.53 9.43 224.93 16.64 227.53 23.39 224.93 29 227.53"/>
                    <polyline className="cls-5" points="4.29 224.93 9.43 227.53 16.64 224.93 23.39 227.53 29 224.93"/>
                </g>
            </g>
        </svg>
        </div>
    )
}

export default Tempmeter;
